import classnames from 'classnames'
import React, { useContext, useEffect,useState } from 'react'
import { MatrixTableContext, MatrixTableContextProvider } from './context'
import { store } from '../../pages/store';

  type Props = {
    initialMatrix?: import('../../types').Matrix
  } & import('react').HTMLAttributes<HTMLDivElement>

  /**
   * Add 4 buttons: 
   * - Cancel to reset the matrix to how it was before changing the values (only when in edit mode)
   * - Edit to make the fields editable (only when not in edit mode)
   * - Clear to completely clear the table
   * - Save to save the table
   * @param param0 
   */
  const MatrixTable: import('react').FC<Omit<Props, 'initialMatrix'>> = ({ className, children, ...props }) => {
  // State ------------------------------------------------------------------- //
  const [{ matrix }, dispatch] = useContext(MatrixTableContext)
  const [ matrixData, setMatrixData]  = useState(matrix);
  const [ flag, setFlag ] = useState(true);

  // Handlers ---------------------------------------------------------------- //
  useEffect(()=>{
    fetch('http://localhost:3000/api/pricing', {
      method: 'GET',
      headers: {
          accept: 'application/json',
          'content-type': 'application/json'
      },
    })
    .then(res => 
      res.json()
    )
    .then(data=> {
      setMatrixData(data);
      dispatch({type: 'GET_ALL_DATA', payload: data });

    })
    .catch(err => console.log('---error', err));
  },[])

  // You can save (to api) the matrix here. Remember to update originalMatrix when done.
  const save = async () => {
    dispatch({type: 'SAVE_JSON'});
  }
  // Effects ----------------------------------------------------------------- //
  // Rendering --------------------------------------------------------------- //


  // const updatematrixtable = 
  //   <table id="customers">
  //       <tr>
  //         <th>months</th>
  //         <th>lite</th>
  //         <th>standard</th>
  //         <th>unlimited</th>
  //         <th>performance</th>
  //       </tr>
  //       <tr>
  //         <td>12months</td>
  //         <td>{matrixData["12months"].lite}</td>
  //         <td>{matrixData["12months"].standard}</td>
  //         <td>{matrixData["12months"].unlimited}</td>
  //       </tr>
  //       <tr>
  //         <td>24months</td>
  //         <td>{matrixData["24months"].lite}</td>
  //         <td>{matrixData["24months"].standard}</td>
  //         <td>{matrixData["24months"].unlimited}</td>
  //       </tr>
  //       <tr>
  //         <td>36months</td>
  //         <td>{matrixData["36months"].lite}</td>
  //         <td>{matrixData["36months"].standard}</td>
  //         <td>{matrixData["36months"].unlimited}</td>
  //       </tr>
  //       <tr>
  //         <td>mtm</td>
  //         <td>{matrixData.mtm.lite}</td>
  //         <td>{matrixData.mtm.standard}</td>
  //         <td>{matrixData.mtm.unlimited}</td>
  //       </tr>
  //     </table>
  const edit = ()=>{
    console.log("editbutton");
    setFlag(false);
  }

  const cancel = ()=>{
    console.log("cancelbutton");
    setFlag(true);
  }

  const editPricing = () => {

  }

  const editbutton = () => (
    <tr>
      <td>12 months</td>
      <td>{matrix["12months"].lite}</td>
      <td>{matrix["12months"].standard}</td>
      <td>{matrix["12months"].unlimited}</td>
      <td ><button onClick={edit}> Edit</button></td>

    </tr>
  )

  const editdetailbutton=
    <tr>
      <td>12 months</td>
      <td><input type='number'  /></td>
      <td><input value={matrix["12months"].standard} /></td>
      <td><input value={matrix["12months"].unlimited}></input></td>
      <td className="performance" >
        <button onClick={editPricing}>OK</button>
        <button onClick={cancel}>Cancel</button>
      </td>
    </tr>

  return (
    <div className={classnames(['container', className])} {...props}>
      <button onClick={save} className="btn btn-success">Save</button>
      {/* <br />
      <br />

      36months lite:
      <input 
        value={matrix["36months"].lite || ''} 
        onChange={(e) => dispatch({
          type: 'SOME_ACTION',
          payload: e.target.value
        })} /> */}
      <table id="customers">
        <tr>
          <th>months</th>
          <th>lite</th>
          <th>standard</th>
          <th>unlimited</th>
          <th>performance</th>
        </tr>

        {flag? editbutton: editdetailbutton}

        <tr>
          <td>24months</td>
          <td>{matrix["24months"].lite}</td>
          <td>{matrix["24months"].standard}</td>
          <td>{matrix["24months"].unlimited}</td>
          <td>
            <button>OK</button>
            <button>Cancel</button>
          </td>
        </tr>
        <tr>
          <td>36months</td>
          <td>{matrix["36months"].lite}</td>
          <td>{matrix["36months"].standard}</td>
          <td>{matrix["36months"].unlimited}</td>
          <td>
            <button>OK</button>
            <button>Cancel</button>
          </td>
        </tr>
        <tr>
          <td>mtm</td>
          <td>{matrix.mtm.lite}</td>
          <td>{matrix.mtm.standard}</td>
          <td>{matrix.mtm.unlimited}</td>
          <td>
            <button>OK</button>
            <button>Cancel</button>
          </td>
        </tr>
      </table>

      <style jsx>{`
        .container {
          margin:0;
          width:100%
        }
        #customers {
          font-family: "Trebuchet MS", Arial, Helvetica, sans-serif;
          border-collapse: collapse;
          width: 100%;
        }

        #customers td, #customers th {
          border: 1px solid #ddd;
          padding: 8px;
        }

        #customers tr:nth-child(even){background-color: #f2f2f2;}

        #customers tr:hover {background-color: #ddd;}

        #customers th {
          padding-top: 12px;
          padding-bottom: 12px;
          text-align: left;
          background-color: #4CAF50;
          color: white;
        }
        .performance {
          display:flex;
        }
      `}</style>
    </div>
  )
}

const MatrixTableWithContext: import('react').FC<Props> = ({ initialMatrix, ...props }) => {
  // You can fetch the pricing here or in pages/index.ts
  // Remember that you should try to reflect the state of pricing in originalMatrix.
  // matrix will hold the latest value (edited or same as originalMatrix)

  return (
    <MatrixTableContextProvider initialMatrix={initialMatrix}>
      <MatrixTable {...props} />
    </MatrixTableContextProvider>
  )
}

export default MatrixTableWithContext
