const defaultPricing = {
  "36months" : {
      "lite" : 2592.8,
      "standard" : 2981.72,
      "unlimited" : 3889.2
  },
  "24months" : {
      "lite" : 3056.09,
      "standard" : 3445.01,
      "unlimited" : 4352.49
  },
  "12months" : {
      "lite" : 3528,
      "standard" : 3916.92,
      "unlimited" : 4824.4
  },
  "mtm" : {
      "lite" : 5880,
      "standard" : 6268.92,
      "unlimited" : 7176.4
  }
}

const fs = require('fs-extra');

export default (req: import('next').NextApiRequest, res: import('next').NextApiResponse) => {
  console.log('---here ', req.body, req.params);
  const file = 'pricing.json';
  fs.outputJson(file, {name: 'JP'})
  .then(() => fs.readJson(file))
  .then(data => {
    console.log(data) // => JP
  })
  .catch(err => {
    console.error(err)
  })
  const pricing = defaultPricing
  res.statusCode = 200
  res.json(pricing)
}
